import React, { useState } from 'react';
// import { FaArrowLeft, FaArrowRight } from 'react-icons/fa';


const items = [
  { icon: '🚗', title: 'Car' },
  { icon: '🚀', title: 'Rocket' },
  { icon: '🏍️', title: 'Bike' },
  { icon: '🚁', title: 'Helicopter' },
  { icon: '🛸', title: 'UFO' },
];

 function Carousel() {
    const [activeIndex, setActiveIndex] = useState(0);

  const rotateLeft = () => {
    setActiveIndex((prev) => (prev - 1 + items.length) % items.length);
  };

  const rotateRight = () => {
    setActiveIndex((prev) => (prev + 1) % items.length);
  };

  return (
    <div className="relative w-full h-[400px] flex flex-col items-center justify-center bg-black text-white overflow-hidden">
      <div className="relative w-full h-[300px] flex items-center justify-center">
        {items.map((item, i) => {
          const diff = (i - activeIndex + items.length) % items.length;

          // Position based on circular arc but only center is shown
          if (i === activeIndex) {
            return (
              <div
                key={i}
                className="absolute flex flex-col items-center justify-center w-40 h-40 bg-white text-black text-3xl font-bold rounded-xl shadow-2xl transition-all duration-500"
              >
                <span className="text-5xl mb-2">{item.icon}</span>
                {item.title}
              </div>
            );
          }

          return null; // Hide all non-active
        })}
      </div>

      {/* Controls */}
      <div className="mt-6 flex gap-8">
        <button onClick={rotateLeft} className="p-3 bg-gray-800 rounded-full hover:bg-gray-600">

        </button>
        <button onClick={rotateRight} className="p-3 bg-gray-800 rounded-full hover:bg-gray-600">
          <FaArrowRight />
        </button>
      </div>
    </div>
  );
}
export default Carousel
