import React from "react";

function Home() {
     window.addEventListener('deviceorientation', function(event) {
      const header = document.getElementById('gyro-header');
      const rotateX = (event.beta - 45) / 5;   // Forward/backward tilt
      const rotateY = (event.gamma) / 5;       // Left/right tilt
      if (header) {
        header.style.transform = `rotateX(${rotateX}deg) rotateY(${rotateY}deg)`;
      }
    });
  return (
    <>
      <div className="w-screen h-screen flex flex-col justify-center items-center z-1000 " style={{perspective}} id="gyro-header" >
        <h1 className=" text-8xl font-[900]  relative inline-block cracked-text text-center">
          SAGE
          <br />
          STACK
          <br />
          SOLUTIONS
        </h1>
        <h2 className="text-lg ">
          We don’t just develop — we accelerate your business with tech,
          traffic, and intelligence.
        </h2>
      </div>
    </>
  );
}

export default Home;
