import React from "react";

function Home() {
        let lastScroll = 0;
    const header = document.getElementById('scroll-header');
if(header )
    window.addEventListener('scroll', () => {
      const currentScroll = window.scrollY;

      if (currentScroll > lastScroll) {
        // Scrolling down → shrink and fade
        header.classList.add('opacity-0', 'scale-50');
        header.classList.remove('opacity-100', 'scale-100');
      } else {
        // Scrolling up → restore
        header.classList.remove('opacity-0', 'scale-50');
        header.classList.add('opacity-100', 'scale-100');
      }

      lastScroll = currentScroll <= 0 ? 0 : currentScroll; // prevent negative scroll
    });
  return (
    <>
      <div id="scroll-header" className="w-screen h-screen flex flex-col justify-center items-center z-1000 animate-float top-10 left-1/2 -translate-x-1/2 transition-header"   >
        <h1 className=" text-8xl font-[900]  relative inline-block cracked-text text-center">
          SAGE
          <br />
          STACK
          <br />
          SOLUTIONS
        </h1>
        <h2 className="text-lg ">
          We don’t just develop — we accelerate your business with tech,
          traffic, and intelligence.
        </h2>
      </div>
      <div className="h-screen">

      </div>
    </>
  );
}

export default Home;
