import { useState, useEffect } from "react";
// import Test2 from "Test2";
import Hero from "./Hero";
import About from "./About";

const sections = [
  { id: 1, text: <Hero /> },
  { id: 2, text: "This is Section 2" },
  { id: 3, text: "Here comes Section 3"},
  { id: 4, text: "Here comes Section 4" },
];

export default function App() {
  const [activeIndex, setActiveIndex] = useState(0);
  const [animating, setAnimating] = useState(false);

  const handleWheel = (e) => {
    if (animating) return;
    setAnimating(true);

    if (e.deltaY > 0 && activeIndex < sections.length - 1) {
      setActiveIndex((prev) => prev + 1);
    } else if (e.deltaY < 0 && activeIndex > 0) {
      setActiveIndex((prev) => prev - 1);
    }

    setTimeout(() => {
      setAnimating(false);
    }, 800); // animation duration
  };

  useEffect(() => {
    window.addEventListener("wheel", handleWheel, { passive: false });
    return () => window.removeEventListener("wheel", handleWheel);
  });

  return (
    <div className="relative w-screen h-screen overflow-hidden">
      {sections.map((section, index) => (
        <div
          key={section.id}
          className={`absolute inset-0 flex items-center justify-center transition-all duration-700 ease-in-out text-white text-4xl font-bold ${section.bg} ${
            index === activeIndex
              ? "opacity-100 translate-y-0 z-10"
              : index < activeIndex
              ? "opacity-0 -translate-y-20 z-0"
              : "opacity-0 translate-y-20 z-0"
          }`}
        >
          {section.text}
        </div>
      ))}
    </div>
  );
}
