          />
          <input
            placeholder="Mobile Number"
            value={clientMobileNo}
            onChange={(e) => {
              setMobileNo(e.target.value);
            }}
            className="bg-transparent focus:outline-none border-[1px] border-opacity-30 border-white rounded-lg placeholder:text-lg text-lg font-light px-2 h-8 placeholder:font-light w-[80%] mb-2"
          />
          <input
            placeholder="Email"
            value={clientEmail}
            onChange={(e) => {
              setEmail(e.target.value);
            }}
            className="bg-transparent focus:outline-none border-[1px] border-opacity-30 border-white rounded-lg placeholder:text-lg text-lg font-light px-2 h-8 placeholder:font-light w-[80%] mb-2"
          />
          <button
            onClick={() => {
