    if (divRef.current) {
      observer.observe(divRef.current);
    }

    return () => {
      if (divRef.current) {
        observer.unobserve(divRef.current);
      }
    };
  }, []);


  useEffect(() => {
    AOS.init({
      duration: 1000, // Animation duration
      once: false, 
      mirror: true,    
    });
  }, []);

