import React from 'react';
// import './orbit.css'; // Contains animation & orbit logic

const items = [
  { icon: 'üöÄ', title: 'Rocket' },
  { icon: 'ü™ê', title: 'Planet' },
  { icon: 'üåô', title: 'Moon' },
  { icon: '‚òÑÔ∏è', title: 'Comet' },
  { icon: 'üõ∞Ô∏è', title: 'Satellite' },
];

 function PlanetOrbitCarousel() {
  return (
    <div className="relative h-[400px] bg-black flex items-center justify-center overflow-hidden">
      {/* Orbit Path Line */}
      <div className="absolute w-[400px] h-[200px] border-t border-gray-600 rounded-b-full opacity-30 bottom-[90px]" />

      {/* Rotating Container */}
      <div className="orbit-container relative w-full h-[300px] flex items-center justify-center">
        {items.map((item, index) => (
          <div
            key={index}
            className="orbit-item w-28 h-28 absolute bg-white/10 border border-white/20 backdrop-blur-md rounded-xl text-white flex flex-col items-center justify-center shadow-md"
            style={{
              '--i': index,
              '--total': items.length,
            }}
          >
            <div className="text-3xl">{item.icon}</div>
            <div className="text-xs mt-1">{item.title}</div>
          </div>
        ))}
      </div>
    </div>
  );
}
export default PlanetOrbitCarousel
