import React, { useState } from 'react';
import { FaArrowLeft, FaArrowRight } from 'react-icons/fa';

const items = ['🚗', '🚀', '🏍️', '🚁', '🛸', '🚤', '🛵'];

 function Carousel() {
  const [angleOffset, setAngleOffset] = useState(0);
  const radius = 200; // Adjust radius for arc size
  const itemAngle = 180 / (items.length - 1); // Spread items in semi-circle

  const rotateLeft = () => setAngleOffset((prev) => prev + itemAngle);
  const rotateRight = () => setAngleOffset((prev) => prev - itemAngle);

  return (
    <div className="relative w-full h-[400px] flex flex-col items-center justify-center bg-black text-white">
      <div className="relative w-full h-[300px]">
        {items.map((item, i) => {
          const angle = i * itemAngle + angleOffset;
          const radians = (angle * Math.PI) / 180;
          const x = radius * Math.cos(radians);
          const y = radius * Math.sin(radians);

          return (
            <div
              key={i}
              className="absolute flex items-center justify-center w-16 h-16 bg-white text-black rounded-full shadow-lg transition-all duration-300"
              style={{
                transform: `translate(${x}px, ${-y}px)`,
                left: '50%',
                top: '80%',
              }}
            >
              {item}
            </div>
          );
        })}
      </div>

      {/* Controls */}
      <div className="mt-6 flex gap-8">
        <button onClick={rotateLeft} className="p-3 bg-gray-800 rounded-full hover:bg-gray-600">
          <FaArrowLeft />
        </button>
        <button onClick={rotateRight} className="p-3 bg-gray-800 rounded-full hover:bg-gray-600">
          <FaArrowRight />
        </button>
      </div>
    </div>
  );
}
export default Carousel
