import React, { useEffect, useState } from "react";
import axios from "axios";

function ContactPage() {
  const [name,setName] = useState('');
  const [mobileNo,setMobileNo] = useState('');
  const [email,setEmail] = useState('');

  useEffect(()=>{
axios.post('http://localhost:8080/client-info',{
  name:name,
  mobileNo:mobileNo,
  email:email
})
  },[])
  return (
    <>

        <div class="flex justify-center items-center relative rounded-3xl overflow-hidden shadow-2xl border border-white/20 glass-effect w-[80vw]">
          <div class="absolute inset-0 bg-gradient-to-br from-white/30 via-white/10 to-white/5 opacity-40"></div>
          <div class="relative z-10 p-6 text-white w-[100%] flex flex-col justify-center items-center">


            <h2 className="text-center mb-6">Contact Us</h2>
            <input placeholder="Name" value={name} onChange={(e)=>{setName(e.target.value)}} className="bg-transparent focus:outline-none border-[1px] border-opacity-30 border-white rounded-lg placeholder:text-lg text-lg font-light px-2 h-8 placeholder:font-light w-[80%] mb-2"/>
            <input placeholder="Mobile Number" value={mobileNo} onChange={(e)=>{setMobileNo(e.target.value)}} className="bg-transparent focus:outline-none border-[1px] border-opacity-30 border-white rounded-lg placeholder:text-lg text-lg font-light px-2 h-8 placeholder:font-light w-[80%] mb-2"/>
            <input placeholder="Email" value={email} onChange={(e)=>{setEmail(e.target.value)}} className="bg-transparent focus:outline-none border-[1px] border-opacity-30 border-white rounded-lg placeholder:text-lg text-lg font-light px-2 h-8 placeholder:font-light w-[80%] mb-2"/>
<button >Submit</button>
        </div>
      </div>
      {/* <div className="bg-white/10 backdrop-blur-md border border-white/20 shadow-lg h-96 w-full">

      </div> */}
    </>
  );
}

export default ContactPage;
