import React, { useEffect, useState } from 'react';

const items = [
  { icon: '🚀' },
  { icon: '🪐' },
  { icon: '🌙'},
  { icon: '☄️'},
  { icon: '🛰️'},
   { icon: '🪐' },
  { icon: '🌙'},
  { icon: '☄️'},
  { icon: '🛰️'},
];

function OrbitCarousel() {
  const [activeIndex, setActiveIndex] = useState(0);
  const radius = 180;
  const visibleItems = 2.5;

  // Autoplay
  useEffect(() => {
    const interval = setInterval(() => {
      setActiveIndex((prev) => (prev + 1) % items.length);
    }, ); // every 2.5s
    return () => clearInterval(interval);
  }, []);

  // Spread across semicircle
  const itemAngle = 180 / (visibleItems * 2);

  return (
    <div className="relative h-[400px] w-full  overflow-hidden flex flex-col items-center justify-center">
      {/* Orbit Arc (lines between elements) */}
      <div className="absolute bottom-[90px] w-[400px] h-[200px] border-t border-gray-600 rounded-b-full opacity-30" />

      {/* Items on Arc */}
      <div className="relative w-full h-[300px] flex items-center justify-center">
        {items.map((item, index) => {
          const relativeIndex =
            (index - activeIndex + items.length) % items.length;

          // Position only 3 items max (center, left, right)
          if (relativeIndex > 2 && relativeIndex < items.length - 2) return null;

          const angle = (relativeIndex - 1) * itemAngle;
          const rad = (angle * Math.PI) / 180;
          const x = radius * Math.cos(rad);
          const y = radius * Math.sin(rad);

          const scale = relativeIndex === 1 ? 1 : 0.8;
          const zIndex = relativeIndex === 1 ? 10 : 5;
          const opacity = relativeIndex === 1 ? 1 : 0.5;

          return (
            <div
              key={index}
              className="absolute shadow-md flex flex-col items-center justify-center transition-all duration-500"
              style={{
                transform: `translate(${x}px, ${-y}px) scale(${scale})`,
                opacity,
                zIndex,
              }}
            >
              <div className="text-4xl">{item.icon}</div>
              {/* <div className="mt-2 text-sm font-semibold">{item.title}</div> */}
            </div>
          );
        })}
      </div>
    </div>
  );
}
export default OrbitCarousel
